import React, { useState, useEffect } from "react";

const StreamListPage = () => {
  const [movies, setMovies] = useState([]);

  // Load saved movies from Local Storage on page load
  useEffect(() => {
    const savedMovies = JSON.parse(localStorage.getItem("movies")) || [];
    setMovies(savedMovies);
  }, []);

  // Save movies to Local Storage every time they update
  const updateMovies = (newMovies) => {
    setMovies(newMovies);
    localStorage.setItem("movies", JSON.stringify(newMovies));
  };

  const [movieTitle, setMovieTitle] = useState("");

  const addMovie = () => {
    if (movieTitle.trim()) {
      const newMovies = [...movies, { title: movieTitle, id: Date.now() }];
      updateMovies(newMovies);
      setMovieTitle(""); // Clear input field
    }
  };

  const deleteMovie = (id) => {
    const updatedMovies = movies.filter((movie) => movie.id !== id);
    updateMovies(updatedMovies);
  };

  return (
    <div>
      <h2>StreamList Page</h2>
      <p>Manage your streaming list below:</p>

      <input
        type="text"
        placeholder="Enter movie title..."
        value={movieTitle}
        onChange={(e) => setMovieTitle(e.target.value)}
      />
      <button onClick={addMovie}>Add</button>

      <ul>
        {movies.map((movie) => (
          <li key={movie.id}>
            {movie.title}{" "}
            <button 
              onClick={() => deleteMovie(movie.id)} 
              style={{ background: "pink", border: "none", cursor: "pointer" }}>
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StreamListPage;
