import React, { useState, useEffect, useRef } from "react";
import { FaCheck, FaEdit, FaTrash } from "react-icons/fa";

const StreamListPage = () => {
  const [movies, setMovies] = useState(() => {
    const savedMovies = localStorage.getItem("movies");
    return savedMovies ? JSON.parse(savedMovies) : [];
  });

  const [input, setInput] = useState("");
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");
  const inputRef = useRef(null);

  useEffect(() => {
    localStorage.setItem("movies", JSON.stringify(movies));
  }, [movies]);

  useEffect(() => {
    if (editId !== null) {
      inputRef.current?.focus();
    }
  }, [editId]);

  const addMovie = () => {
    if (input.trim() === "") return;
    setMovies([...movies, { id: Date.now(), title: input.trim(), completed: false }]);
    setInput("");
  };

  const deleteMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const toggleComplete = (id) => {
    setMovies(
      movies.map((movie) =>
        movie.id === id ? { ...movie, completed: !movie.completed } : movie
      )
    );
  };

  const startEditing = (id, title) => {
    setEditId(id);
    setEditText(title);
  };

  const saveEdit = () => {
    if (editText.trim() === "") return;
    setMovies(
      movies.map((movie) =>
        movie.id === editId ? { ...movie, title: editText.trim() } : movie
      )
    );
    setEditId(null);
    setEditText("");
  };

  return (
    <div className="streamlist-container">
      <h2>ðŸŽ¬ My StreamList</h2>
      <p>Keep track of your must-watch movies!</p>

      <div className="input-group">
        <input
          type="text"
          placeholder="Enter movie title..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && addMovie()}
        />
        <button onClick={addMovie}>âž• Add</button>
      </div>

      <ul className="movie-list">
        {movies.map((movie) => (
          <li key={movie.id} className={movie.completed ? "completed" : ""}>
            {editId === movie.id ? (
              <>
                <input
                  ref={inputRef}
                  type="text"
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  onKeyDown={(e) => e.key === "Enter" && saveEdit()}
                />
                <button onClick={saveEdit}>ðŸ’¾ Save</button>
              </>
            ) : (
              <>
                <span className="movie-title">
                  {movie.title}
                </span>

                <div className="movie-actions">
                  <FaCheck onClick={() => toggleComplete(movie.id)} className="icon check-icon" />
                  <FaEdit onClick={() => startEditing(movie.id, movie.title)} className="icon edit-icon" />
                  <FaTrash onClick={() => deleteMovie(movie.id)} className="icon delete-icon" />
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StreamListPage;
