import React, { useState, useEffect } from "react";
import { FaCheck, FaEdit, FaTrash } from "react-icons/fa";

const StreamListPage = () => {
  const [movies, setMovies] = useState(() => {
    const savedMovies = localStorage.getItem("movies");
    return savedMovies ? JSON.parse(savedMovies) : [];
  });

  const [input, setInput] = useState("");
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  useEffect(() => {
    localStorage.setItem("movies", JSON.stringify(movies));
  }, [movies]);

  const addMovie = () => {
    if (input.trim() === "") return;
    setMovies([...movies, { id: Date.now(), title: input.trim(), completed: false }]);
    setInput("");
  };

  const deleteMovie = (id) => {
    setMovies(movies.filter((movie) => movie.id !== id));
  };

  const toggleComplete = (id) => {
    setMovies(
      movies.map((movie) =>
        movie.id === id ? { ...movie, completed: !movie.completed } : movie
      )
    );
  };

  return (
    <div className="streamlist-container">
      <h2>My StreamList</h2>
      <div className="input-group">
        <input
          type="text"
          placeholder="Enter movie title..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={addMovie}>Add</button>
      </div>

      <ul className="movie-list">
        {movies.map((movie) => (
          <li key={movie.id} className={movie.completed ? "completed" : ""}>
            {movie.title}
            <div className="movie-actions">
              <FaCheck onClick={() => toggleComplete(movie.id)} className="icon check-icon" />
              <FaEdit className="icon edit-icon" />
              <FaTrash onClick={() => deleteMovie(movie.id)} className="icon delete-icon" />
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StreamListPage;
