import axios from "axios";

const API_KEY = process.env.REACT_APP_TMDB_API_KEY;
const BASE_URL = "https://api.themoviedb.org/3";

// ✅ Universal function to fetch movies by category
export const fetchMovies = async (category = "popular") => {
  if (!API_KEY) {
    console.error("Error: Missing TMDB API Key.");
    return [];
  }

  try {
    const response = await axios.get(`${BASE_URL}/movie/${category}`, {
      params: { api_key: API_KEY, language: "en-US", page: 1 },
    });

    return response.data.results || [];
  } catch (error) {
    console.error(`Error fetching ${category} movies:`, error.message);
    return [];
  }
};
